package com.example.hw_3_4;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.RecyclerView;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.example.hw_3_4.databinding.FragmentFirstBinding;

import java.util.ArrayList;

public class FirstFragment extends Fragment implements OnItemClick {

    private FragmentFirstBinding binding;
    private ContinentAdapter adapter;
    private ArrayList<ContinentModel> continentsList = new ArrayList<>();

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        binding = FragmentFirstBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        loadData();
        adapter = new ContinentAdapter(continentsList, this);
        binding.rvContinents.setAdapter(adapter);
    }

    private void loadData() {
        continentsList.add(new ContinentModel("Asia", "data:image/jpeg;base64,"));
        continentsList.add(new ContinentModel("Africa", "data:image/jpeg;base64,"));
        continentsList.add(new ContinentModel("Europe", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSBte5RMce4wmddsAQ3kLn-wS9xyoG0qfKABA&usqp=CAU"));
        continentsList.add(new ContinentModel("North America", "data:image/png;base64,"));
        continentsList.add(new ContinentModel("South America", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSR71Blo33r0LuVr6cLKUe6XbtETLd04_avUw&usqp=CAU"));
        continentsList.add(new ContinentModel("Australia", "data:image/png;base64,"));
    }

    @Override
    public void onItemClick(int position) {
        Bundle bundle = new Bundle();
        bundle.putInt("key", position);

        SecondFragment secondFragment = new SecondFragment();
        secondFragment.setArguments(bundle);
        requireActivity().getSupportFragmentManager().beginTransaction().replace(R.id.container, secondFragment).addToBackStack(null).commit();
    }
}